/// <summary>
/// Gets inventory dimension group controller
/// </summary>
/// <returns>An instance of <c>InventDimCtrl_Frm_EditDimensions</c></returns>
public InventDimCtrl_Frm_EditDimensions inventDimSetupObject()
{
    return inventDimFormSetup;
}

    void updateDesign(InventDimFormDesignUpdate mode)
    {
        InventDimParm inventDimParmVisible;

        switch (mode)
        {
            case InventDimFormDesignUpdate::Init:
                if (!inventDimFormSetup)
                {
                    inventDimFormSetup = InventDimCtrl_Frm_EditDimensions::newFromForm(element);
                    inventDimFormSetup.parmSkipOnHandLookUp(true);
                    inventDimFormSetup.parmDimParmVisibleGrid(inventDimParmVisible);
                }
                break;

            case InventDimFormDesignUpdate::Active:
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(ITVTRentalLine.ItemId));
                inventDimFormSetup.formSetControls(true);
                break;

            case InventDimFormDesignUpdate::FieldChange:
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(ITVTRentalLine.ItemId));
                inventDim.clearNotSelectedDim(inventDimFormSetup.parmDimParmEnabled());
                inventDimFormSetup.formSetControls(true);
                break;

            default :
                throw error(strFmt("@SYS54195",funcName()));
        }
    }

    private void initReturnFromRental()
    {
        ITVTRentalParameters rentalParameters = ITVTRentalParameters::find();

        CtrlPostingDate.dateValue(today());
        CtrlRentalRunForOpenPeriods.value(rentalParameters.RentalRunForOpenPeriods);
        CtrlUseConfirmedRentalEndDateForEndRental.value(rentalParameters.UseConfirmedRentalEndDateForEndRental);
    }

        public void init()
        {
            super();

            QueryBuildDataSource qbdsSalesTable = SalesTable_q.dataSourceTable(tableNum(SalesTable));

            qbdsSalesTable.clearLinks();
            qbdsSalesTable.clearDynalinks();

            custAccountQueryFilter = SalesTable_q.addQueryFilter(qbdsSalesTable, fieldStr(SalesTable, CustAccount));
            custRefQueryFilter = SalesTable_q.addQueryFilter(qbdsSalesTable, fieldStr(SalesTable, CustomerRef));
            dlvModeQueryFilter = SalesTable_q.addQueryFilter(qbdsSalesTable, fieldStr(SalesTable, DlvMode));
        }

        public void executeQuery()
        {
            custAccountQueryFilter.value(CtrlCustAccount.valueStr());
            custRefQueryFilter.value(CtrlCustRef.valueStr());
            dlvModeQueryFilter.value(CtrlDlvModeId.valueStr());

            super();
        }

        public int active()
        {
            int ret;

            ret = super();

            QueryBuildDataSource qbdsRentalLine = ITVTRentalLine_q.dataSourceTable(tableNum(ITVTRentalLine));

            SysQuery::findOrCreateRange(qbdsRentalLine, fieldNum(ITVTRentalLine, SalesId)).value(salesTable.SalesId);

            return ret;
        }

    public void init()
    {
        super();

        QueryBuildDataSource qbdsInventLocation = InventLocation_q.dataSourceTable(tableNum(InventLocation));

        qbdsInventLocation.clearLinks();
        qbdsInventLocation.addLink(fieldNum(SalesTable, ITVTProjInventLocationId), fieldNum(InventLocation, InventLocationId));
    }

    public void init()
    {
        super();

        QueryBuildDataSource qbdsLogisticsEntityPostalAddressView = LogisticsEntityPostalAddressView_q.dataSourceTable(tableNum(LogisticsEntityPostalAddressView));
        
        qbdsLogisticsEntityPostalAddressView.addLink(fieldNum(InventLocation, RecId), fieldNum(LogisticsEntityPostalAddressView, Entity));
        qbdsLogisticsEntityPostalAddressView.addRange(fieldNum(LogisticsEntityPostalAddressView, IsPrimary)).value(SysQuery::value(NoYes::Yes));
        qbdsLogisticsEntityPostalAddressView.addRange(fieldNum(LogisticsEntityPostalAddressView, EntityType )).value(SysQuery::value(LogisticsLocationEntityType::Warehouse));
    }

        public void init()
        {
            super();

            QueryBuildDataSource qbdsRentalOrderLinesView = ITVTRentalOrderLinesView_q.dataSourceTable(tableNum(ITVTRentalOrderLinesView));

            qbdsRentalOrderLinesView.addLink(fieldNum(SalesTable, SalesId), fieldNum(ITVTRentalOrderLinesView, SalesId));

            qbdsRentalOrderLinesView.addRange(fieldNum(ITVTRentalOrderLinesView, PostedRentalFromDate)).value(SysQuery::valueNot(dateNull()));
            qbdsRentalOrderLinesView.addRange(fieldNum(ITVTRentalOrderLinesView, PendingReturnQuantity)).value('>0');
        }

        public void executeQuery()
        {
            QueryBuildDataSource qbdsRentalOrderLinesView = ITVTRentalOrderLinesView_q.dataSourceTable(tableNum(ITVTRentalOrderLinesView));
            
            if (CtrlDaysBefore.valueStr()
                || CtrlDaysAfter.valueStr())
            {
                SysQuery::findOrCreateRange(qbdsRentalOrderLinesView, fieldNum(ITVTRentalOrderLinesView, ReturnDate)).value(SysQueryRangeUtil::dayRange(-str2Int(CtrlDaysBefore.valueStr()), str2Int(CtrlDaysAfter.valueStr())));
            }
            else
            {
                qbdsRentalOrderLinesView.clearRange(fieldNum(ITVTRentalOrderLinesView, ReturnDate));
            }

            SysQuery::findOrCreateRange(qbdsRentalOrderLinesView, fieldNum(ITVTRentalOrderLinesView, ItemId)).value(CtrlItemId.valueStr());
            SysQuery::findOrCreateRange(qbdsRentalOrderLinesView, fieldNum(ITVTRentalOrderLinesView, InventLocationId)).value(CtrlWarehouseRange.valueStr());

            super();
        }

        public int active()
        {
            int ret;
        
            ret = super();

            element.updateDesign(InventDimFormDesignUpdate::Active);

            return ret;
        }

        public void init()
        {
            super();

            QueryBuildDataSource qbdsRentalLine = ITVTRentalLine_q.dataSourceTable(tableNum(ITVTRentalLine));

            qbdsRentalLine.addRange(fieldNum(ITVTRentalLine, PostedRentalFromDate)).value(SysQuery::valueNot(dateNull()));
            qbdsRentalLine.addRange(fieldNum(ITVTRentalLine, PendingReturnQuantity)).value('>0');

            dateRangeQueryFilter = ITVTRentalLine_q.addQueryFilter(qbdsRentalLine, fieldStr(ITVTRentalLine, ReturnDate));
            itemIdQueryFilter = ITVTRentalLine_q.addQueryFilter(qbdsRentalLine, fieldStr(ITVTRentalLine, ItemId));
        }

        public void executeQuery()
        {
            if (CtrlDaysBefore.valueStr()
                || CtrlDaysAfter.valueStr())
            {
                dateRangeQueryFilter.value(SysQueryRangeUtil::dayRange(-str2Int(CtrlDaysBefore.valueStr()), str2Int(CtrlDaysAfter.valueStr())));
            }
            else
            {
                dateRangeQueryFilter.value(SysQueryRangeUtil::dateRange(dateNull(), dateMax()));
            }
            
            itemIdQueryFilter.value(CtrlItemId.valueStr());

            super();
        }

        public void init()
        {
            super();

            QueryBuildDataSource qbdsInventDim = InventDim_q.dataSourceTable(tableNum(InventDim));

            warehouseRangeQueryFilter = InventDim_q.addQueryFilter(qbdsInventDim, fieldStr(InventDim, InventLocationId));
        }

        public void executeQuery()
        {
            super();

            warehouseRangeQueryFilter.value(CtrlWarehouseRange.valueStr());
        }

