        public void clicked()
        {
            MultiSelectionHelper helper = MultiSelectionHelper::createFromCaller(element);
            SalesTable salesTableLocal = helper.getFirst();

            while (salesTableLocal)
            {
                ITVTPostRentalReturnController::runSalesOrder(
                    salesTableLocal.SalesId,
                    CtrlPostingDate.dateValue(),
                    CtrlToWarehouse.valueStr(),
                    CtrlWMSLocationId.valueStr(),
                    CtrlRentalRunForOpenPeriods.value(),
                    CtrlUseConfirmedRentalEndDateForEndRental.value());

                salesTableLocal = helper.getNext();
            }
        }
