[Form]
public class ITVTRentalParameters extends FormRun
{
    boolean                     runExecuteDirect;
    TmpIdRef                    tmpIdRef;
    NumberSeqScope              scope;
    NumberSeqApplicationModule  numberSeqApplicationModule;
    container                   numberSequenceModules;
    NumberSeqReference          numberSeqReferenceRebate;

}

protected void initItemAssignmentsFields()
{
    if (ITVTRentalItemAssetGroup.ItemCode == TableGroupAll::All)
    {
        ITVTRentalItemAssetGroup_ds.object(fieldNum(ITVTRentalItemAssetGroup, ItemRelation)).mandatory(false);
        ITVTRentalItemAssetGroup_ds.object(fieldNum(ITVTRentalItemAssetGroup, ItemRelation)).allowEdit(false);
    }
    else
    {
        ITVTRentalItemAssetGroup_ds.object(fieldNum(ITVTRentalItemAssetGroup, ItemRelation)).mandatory(true);
        ITVTRentalItemAssetGroup_ds.object(fieldNum(ITVTRentalItemAssetGroup, ItemRelation)).allowEdit(true);
    }
}

protected void initRentalParametersFields()
{
    ITVTRentalParameters_ds.object(fieldNum(ITVTRentalParameters, DepreciateInRentalEnd)).allowEdit(ITVTRentalParameters.DepreciateInventory == NoYes::Yes);
    
    if (ITVTRentalParameters.DepreciateInventory == NoYes::No)
    {
        ITVTRentalParameters.DepreciateInRentalEnd = NoYes::No;
    }
}

        public int active()
        {
            int ret;
        
            ret = super();

            element.initRentalParametersFields();
        
            return ret;
        }

        public int active()
        {
            int ret;
        
            ret = super();

            element.initItemAssignmentsFields();
        
            return ret;
        }
